const [users, setUsers] = useState(execArray)

  function filterExecutive() {

    const execArray = [];

    for (let i = 0; i < users.length; i++) {
      const employee = users[i];
      if (users.role === "EXECUTIVE") {
        execArray.push(employee);
      }
    }
    setUsers(execArray);
    console.log(execArray)
  }

  function filterNone() {
    setUsers(users);
  }

  return (
    <div className="app">
      <h1 className="header text-center">User Directory</h1>
      <button
        onClick={() => filterExecutive()}
        className="default middle-button"
      >Filter by Executive</button>
      <button
        onClick={() => filterNone()}
        className="default middle-button"
      >Reset Filter</button>
      <div className="container">
        <div className="filter">
          <Employees key={users.id} />
          <div class="col">
          </div>
        </div>
      </div>
    </div>
  )
}



