import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import "./styles.css";
import { users } from "./data"
import Employees from "./components/EmployeeCard";



// The application (APP) needs to render a TABLE of USERS on page load



export default function App() {
  const [cards, setCards] = useState(users)

  function filterExecutive() {
    const execArray = [];
    for (let i = 0; i < users.length; i++) {
      const employee = users[i];
      if (users.role === "EXECUTIVE") {
        execArray.push(employee);
      }
    }
    setCards(execArray);
  }

  return (
    <div className="app">
      <h1 className="header text-center">User Directory</h1>
      <button
              onClick={() => filterExecutive()}
              className="default middle-button"
            >Filter by Executive</button>
      <div className="container">
        <div className="filter">
          <Employees />
          <div class="col">
          </div>
        </div>
      </div>
    </div>
  )
}



// APP renders:

// USER DIRECTORY:
//                    (SORT) (FILTER)
//  
// ID  NAME       DEPARTMENT  ROLE           EMAIL (WHAT IS THIS COMPONENT CALLED?)
// 1   ALEXANDER  SALES       SALES MANAGER  alex@fakeemail
