import React, { Component } from "react";
import EmployeeCard from "./components/EmployeeCard/EmployeeCard"
import Wrapper from "./components/Wrapper/Wrapper";
import Title from "./components/Title/Title";
import {users} from "./data";
import "./App.css";

// conversion to Class based programming w/ help of tutor

class App extends Component {
  // Setting this.state.friends to the users array
  state = {
    users
  };

  removeEmployee = id => 
  {
    // Filter this.state.friends for friends with an id not equal to the id being removed
    const employees = this.state.employees.filter(friend => friend.id !== id);
    // Set this.state.friends equal to the new friends array
    this.setState({ employees });
  };

  // Map over this.state.users and render a user card component for each user object
  render() {
    return (
      <Wrapper>
        <Title>Employee Directory</Title>
        {this.state.users.map(user => (
          <EmployeeCard
            removeEmployee ={this.removeEmployee}
            id={user.id}
            key={user.id}
            name={user.name}
            department={user.department}
            role={user.role}
            email={user.email}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;




