import React from "react";

export default function Sort(users) {
    return (
        <div className="container">
            <div className="row">
                <div>Sort Employees By:</div>
            </div>
            <div className="row">
                <div className="col">
                    <select className="form-control " onChange={({ target }) => users.onChange(target.value)}>
                        <option>ID</option>
                        <option>NAME</option>
                        <option>DEPT</option>
                        <option>ROLE</option>
                    </select>
                </div>
            </div>
        </div >
    );
}





